import React, { useEffect } from 'react'
import '../Profile/profile.scss'
import mouseimg from '../images/mouse.png'
import InstagramIcon from '@mui/icons-material/Instagram';
import LinkedInIcon from '@mui/icons-material/LinkedIn';
import FacebookIcon from '@mui/icons-material/Facebook';
import AttachEmailIcon from '@mui/icons-material/AttachEmail';
import Typical from 'react-typical'
import amitimg from '../images/amit yadav-1.png'
export default function Profile() {
    useEffect(() => {

        const rippleSettings = {
            maxSize: 100,
            animationSpeed: 5,
            strokeColor: [148, 217, 255],
        };

        const canvasSettings = {
            blur: 8,
            ratio: 1,
        };

        function Coords(x, y) {
            this.x = x || null;
            this.y = y || null;
        }

        const Ripple = function Ripple(x, y, circleSize, ctx) {
            this.position = new Coords(x, y);
            this.circleSize = circleSize;
            this.maxSize = rippleSettings.maxSize;
            this.opacity = 1;
            this.ctx = ctx;
            this.strokeColor = `rgba(${Math.floor(rippleSettings.strokeColor[0])},
    ${Math.floor(rippleSettings.strokeColor[1])},
    ${Math.floor(rippleSettings.strokeColor[2])},
    ${this.opacity})`;

            this.animationSpeed = rippleSettings.animationSpeed;
            this.opacityStep = (this.animationSpeed / (this.maxSize - circleSize)) / 2;
        };

        Ripple.prototype = {
            update: function update() {
                this.circleSize = this.circleSize + this.animationSpeed;
                this.opacity = this.opacity - this.opacityStep;
                this.strokeColor = `rgba(${Math.floor(rippleSettings.strokeColor[0])},
      ${Math.floor(rippleSettings.strokeColor[1])},
      ${Math.floor(rippleSettings.strokeColor[2])},
      ${this.opacity})`;
            },
            draw: function draw() {
                this.ctx.beginPath();
                this.ctx.strokeStyle = this.strokeColor;
                this.ctx.arc(this.position.x, this.position.y, this.circleSize, 0,
                    2 * Math.PI);
                this.ctx.stroke();
            },
            setStatus: function setStatus(status) {
                this.status = status;
            },
        };

        const canvas = document.querySelector('');
        const ctx = canvas.getContext('2d');
        const ripples = [];

        const height = document.body.clientHeight;
        const width = document.body.clientWidth;

        const rippleStartStatus = 'start';

        const isIE11 = !!window.MSInputMethodContext && !!document.documentMode;

        canvas.style.filter = `blur(${canvasSettings.blur}px)`;

        canvas.width = width * canvasSettings.ratio;
        canvas.height = height * canvasSettings.ratio;

        canvas.style.width = `${width}px`;
        canvas.style.height = `${height}px`;

        let animationFrame;

        // Add GUI settings
        const addGuiSettings = () => {
            const gui = new dat.GUI();
            gui.add(rippleSettings, 'maxSize', 0, 1000).step(1);
            gui.add(rippleSettings, 'animationSpeed', 1, 30).step(1);
            gui.addColor(rippleSettings, 'strokeColor');

            const blur = gui.add(canvasSettings, 'blur', 0, 20).step(1);
            blur.onChange((value) => {
                canvas.style.filter = `blur(${value}px)`;
            });
        };

        addGuiSettings();

        // Function which is executed on mouse hover on canvas
        const canvasMouseOver = (e) => {
            const x = e.clientX * canvasSettings.ratio;
            const y = e.clientY * canvasSettings.ratio;
            ripples.unshift(new Ripple(x, y, 2, ctx));
        };

        const animation = () => {
            ctx.clearRect(0, 0, canvas.width, canvas.height);

            const length = ripples.length;
            for (let i = length - 1; i >= 0; i -= 1) {
                const r = ripples[i];

                r.update();
                r.draw();

                if (r.opacity <= 0) {
                    ripples[i] = null;
                    delete ripples[i];
                    ripples.pop();
                }
            }
            animationFrame = window.requestAnimationFrame(animation);
        };

        animation();
        canvas.addEventListener('mousemove', canvasMouseOver);


    }, [])





    return (
        <div className='Profile'>
            <img className='mouseimg' src={mouseimg} alt={mouseimg} />
            <div className='profile_left'>

                <ul>
                    <li><a href='https://www.instagram.com/amit___545/' target="_blank"><InstagramIcon /></a></li>
                    <li> <a href='https://www.linkedin.com/in/amit-kumar-yadav-81a682224/' target="_blank"><LinkedInIcon /></a></li>
                    <li><a href='https://www.facebook.com/545amit' target="_blank"><FacebookIcon /></a></li>
                    <li><a href="mailto:amitky545@gmail.com?subject = Feedback&body = Message " target="_blank"><AttachEmailIcon /></a></li>

                </ul>
                <div className='User_Name'>
                    <h1>Amit kumar yadav</h1>
                </div>
                <div className='User_Position'>
                    {" "}
                    <h2 style={{ color: 'gold' }}>
                        {" "}
                        <Typical

                            steps={['Full Stack Developer', 3000, 'Back End Developer', 3000, 'Front End developers', 3000]}
                            loop={Infinity}

                        />
                    </h2>
                </div>
                <div className='User_content'>Full stack web  developer with background knowledge of MERN stacks with redux, along with a knack of building applications with utmost efficiency.
                    Strong professional with a BSC willing to be an asset for an organization.</div>

                <div className='Button'>
                    <button className='Hire_me'><a style={{ color: 'black', textDecoration: 'none' }} href='#contact'>Bring In</a></button>
                    <button className='get_resume'> Resume</button>
                </div>
            </div>

            <div className='profile_right'>
                <div className='user_pic'>
                    <img src={amitimg} alt={amitimg} style={{ height: '100%', width: '100%', borderRadius: '50%' }} />
                </div>
            </div>
        </div >
    )
}
